let draggedElement = null;
let dragStart = {
  mouseX: 0,
  mouseY: 0,
  elementX: 0,
  elementY: 0,
  rotation: 0,
  startAngle: 0,
};
let currentRotation = {};
let isRotating = false;
const PROXIMITY_THRESHOLD = 120;
const ANIMAL_SIZE = 80;

function init() {
  const gameArea = document.getElementById("gameArea");
  const pool = document.getElementById("pool");
  const poolRect = pool.getBoundingClientRect();
  const gameRect = gameArea.getBoundingClientRect();

  animals.forEach((animal, index) => {
    const div = document.createElement("div");
    div.className = "animal";
    div.dataset.id = animal.id;
    div.dataset.category = animal.category;
    div.innerHTML = animal.svg;

    const x = poolRect.left - gameRect.left + 20 + (index % 2) * 100;
    const y = poolRect.top - gameRect.top + 60 + Math.floor(index / 2) * 100;

    div.style.left = `${x}px`;
    div.style.top = `${y}px`;

    currentRotation[animal.id] = 45 * index;
    updateRotation(div, currentRotation[animal.id]);

    div.addEventListener("mousedown", startDrag);
    gameArea.appendChild(div);
  });

  document.addEventListener("mousemove", drag);
  document.addEventListener("mouseup", endDrag);
}

function updateRotation(element, rotation) {
  element.style.transform = `rotate(${rotation}deg)`;
  element.style.setProperty("--rotation", `${rotation}deg`);
}

function getElementCenter(element) {
  const rect = element.getBoundingClientRect();
  return {
    x: rect.left + rect.width / 2,
    y: rect.top + rect.height / 2,
  };
}

function getAngle(center, point) {
  return (Math.atan2(point.y - center.y, point.x - center.x) * 180) / Math.PI;
}

function startDrag(e) {
  draggedElement = e.target.closest(".animal");
  if (!draggedElement) return;

  dragStart.mouseX = e.clientX;
  dragStart.mouseY = e.clientY;
  dragStart.elementX = parseFloat(draggedElement.style.left) || 0;
  dragStart.elementY = parseFloat(draggedElement.style.top) || 0;
  dragStart.rotation = currentRotation[draggedElement.dataset.id];
  isRotating = false;

  draggedElement.classList.add("dragging");
  draggedElement.style.zIndex = "1000";

  e.preventDefault();
}

function drag(e) {
  if (!draggedElement) return;
  e.preventDefault();

  if (e.ctrlKey) {
    if (!isRotating) {
      isRotating = true;
      const center = getElementCenter(draggedElement);
      dragStart.startAngle = getAngle(center, { x: e.clientX, y: e.clientY });
    }

    const center = getElementCenter(draggedElement);
    const currentAngle = getAngle(center, { x: e.clientX, y: e.clientY });
    const angleDelta = currentAngle - dragStart.startAngle;
    const rotation = dragStart.rotation + angleDelta;

    currentRotation[draggedElement.dataset.id] = rotation;
    updateRotation(draggedElement, rotation);
  } else {
    if (isRotating) {
      isRotating = false;
      dragStart.rotation = currentRotation[draggedElement.dataset.id];
      dragStart.mouseX = e.clientX;
      dragStart.mouseY = e.clientY;
      dragStart.elementX = parseFloat(draggedElement.style.left) || 0;
      dragStart.elementY = parseFloat(draggedElement.style.top) || 0;
    }

    const gameArea = document.getElementById("gameArea");
    const gameRect = gameArea.getBoundingClientRect();

    const deltaX = e.clientX - dragStart.mouseX;
    const deltaY = e.clientY - dragStart.mouseY;

    let newX = dragStart.elementX + deltaX;
    let newY = dragStart.elementY + deltaY;

    newX = Math.max(0, Math.min(newX, gameRect.width - ANIMAL_SIZE));
    newY = Math.max(0, Math.min(newY, gameRect.height - ANIMAL_SIZE));

    draggedElement.style.left = `${newX}px`;
    draggedElement.style.top = `${newY}px`;
  }
}

function endDrag(e) {
  if (!draggedElement) return;

  draggedElement.classList.remove("dragging");
  draggedElement.style.zIndex = "10";

  checkWin();
  draggedElement = null;
  isRotating = false;
}

function getAnimalCenter(animal) {
  const rect = animal.getBoundingClientRect();
  return {
    x: rect.left + rect.width / 2,
    y: rect.top + rect.height / 2,
  };
}

function getDistance(pos1, pos2) {
  const dx = pos1.x - pos2.x;
  const dy = pos1.y - pos2.y;
  return Math.sqrt(dx * dx + dy * dy);
}

function groupAnimalsByCategory(animals) {
  const groups = {};
  animals.forEach((animal) => {
    const category = animal.dataset.category;
    if (!groups[category]) groups[category] = [];
    groups[category].push(animal);
  });
  return groups;
}

function isInWorkspace(animal) {
  const workspace = document.getElementById("workspace");
  const animalRect = animal.getBoundingClientRect();
  const workspaceRect = workspace.getBoundingClientRect();

  const animalCenterX = animalRect.left + animalRect.width / 2;
  const animalCenterY = animalRect.top + animalRect.height / 2;

  return (
    animalCenterX >= workspaceRect.left &&
    animalCenterX <= workspaceRect.right &&
    animalCenterY >= workspaceRect.top &&
    animalCenterY <= workspaceRect.bottom
  );
}

function areSameCategoryGrouped(group) {
  if (group.length === 1) return true;

  for (let i = 0; i < group.length; i++) {
    const pos1 = getAnimalCenter(group[i]);
    let hasNearby = false;

    for (let j = 0; j < group.length; j++) {
      if (i === j) continue;
      const pos2 = getAnimalCenter(group[j]);
      const distance = getDistance(pos1, pos2);

      if (distance < PROXIMITY_THRESHOLD) {
        hasNearby = true;
        break;
      }
    }

    if (!hasNearby) {
      return false;
    }
  }
  return true;
}

function areDifferentCategoriesSeparated(groups) {
  const categories = Object.keys(groups);

  for (let i = 0; i < categories.length; i++) {
    for (const animal of groups[categories[i]]) {
      const pos = getAnimalCenter(animal);

      for (let j = 0; j < categories.length; j++) {
        if (i === j) continue;

        for (const otherAnimal of groups[categories[j]]) {
          const otherPos = getAnimalCenter(otherAnimal);
          const distance = getDistance(pos, otherPos);

          if (distance < PROXIMITY_THRESHOLD) {
            return false;
          }
        }
      }
    }
  }
  return true;
}

function showWinAnimation(animals) {
  animals.forEach((animal) => animal.classList.add("success-animation"));
  document.getElementById("successMsg").classList.add("show");
  setTimeout(() => {
    document.getElementById("successMsg").classList.remove("show");
  }, 3000);
}

function checkWin() {
  const gameArea = document.getElementById("gameArea");
  const allAnimals = gameArea.querySelectorAll(".animal");
  const animalsInWorkspace = Array.from(allAnimals).filter(isInWorkspace);

  if (animalsInWorkspace.length !== animals.length) {
    return;
  }

  const groups = groupAnimalsByCategory(animalsInWorkspace);

  for (const category in groups) {
    if (!areSameCategoryGrouped(groups[category])) {
      return;
    }
  }

  if (!areDifferentCategoriesSeparated(groups)) {
    return;
  }

  setTimeout(() => showWinAnimation(animalsInWorkspace), 300);
}

init();
